import React, { useState, useEffect } from 'react'; 
import { motion, useAnimation } from 'framer-motion'; 
import { useInView } from 'react-intersection-observer';
import { FaWifi, FaBriefcase, FaUtensils, FaMapMarkerAlt } from 'react-icons/fa'; 

const GallerySection = () => {
  const [hoveredCategory, setHoveredCategory] = useState(null);
  const [isCtaHovered, setIsCtaHovered] = useState(false);

  const controls = useAnimation();
  const [ref, inView] = useInView({
    threshold: 0.1, 
    triggerOnce: false, 
  });

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
        delayChildren: 0.3, 
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 50 }, 
    visible: { opacity: 1, y: 0, transition: { duration: 0.6, ease: "easeOut" } }, 
  };

  useEffect(() => {
    if (inView) {
      controls.start("visible"); 
    } else {
      controls.start("hidden"); 
    }
  }, [controls, inView]); 


  const sectionStyle = {
    padding: '80px 20px',
    background: 'linear-gradient(135deg, #ffeb3b, #d4af37)', 
    textAlign: 'center',
    borderRadius: '12px',
    boxShadow: '0 8px 20px rgba(0, 0, 0, 0.1)',
    margin: '40px auto', 
    maxWidth: '1200px', 
    position: 'relative', 
    overflow: 'hidden', 
  };

  const titleStyle = {
    fontSize: '3.5em',
    color: '#9b111e', 
    marginBottom: '50px',
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.15)',
    letterSpacing: '1.2px',
    fontFamily: 'YourPrimaryFont, sans-serif', 
  };

  const categoriesStyle = {
    display: 'flex',
    justifyContent: 'center',
    gap: '25px',
    marginBottom: '50px',
    flexWrap: 'wrap',
  };

  const categoryButtonStyle = {
    padding: '18px 35px',
    backgroundColor: '#fff',
    color: '#777',
    border: '2px solid #eee8aa',
    borderRadius: '10px',
    cursor: 'pointer',
    fontSize: '1.1em',
    transition:
      'background-color 0.3s ease-in-out, color 0.3s ease-in-out, border-color 0.3s ease-in-out, transform 0.2s ease', // Keep CSS transitions for hover
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.05)',
    fontWeight: '500',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    overflow: 'hidden',
    position: 'relative',
  };

  const categoryImageStyle = {
    width: '120px',
    height: '120px',
    borderRadius: '50%',
    marginBottom: '15px',
    objectFit: 'cover',
    transition: 'transform 0.3s ease', 
  };

   const categoryTextStyle = {
    marginTop: '10px',
    fontSize: '0.9em',
    color: 'inherit', 
  };


  const ctaButtonStyle = {
    padding: '18px 35px',
    backgroundColor: '#9b111e', 
    color: '#fff',
    border: 'none',
    borderRadius: '10px',
    cursor: 'pointer',
    fontSize: '1.2em',
    textDecoration: 'none',
    transition: 'background-color 0.3s ease-in-out, transform 0.2s ease', 
    boxShadow: '0 6px 12px rgba(0, 0, 0, 0.15)',
    fontWeight: '600',
    display: 'inline-block', 
  };


  const photoCategories = [
    { name: "Rooms", imageUrl: "https://static.wixstatic.com/media/c837a6_1c6101ab26bd4ce39dc24d05df83afe1~mv2.jpg/v1/fill/w_432,h_304,al_c,q_80,usm_0.66_1.00_0.01,enc_avif,quality_auto/Aura_Restaurant.jpg" },
    { name: "Restaurant", imageUrl: "https://images.unsplash.com/photo-1513104890138-7c749659a591?q=80&w=2070&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" },
    { name: "Lobby & Reception", imageUrl: "data:image/jpeg;base64," },
    { name: "Events", imageUrl: "data:image/jpeg;base64," }, 
  ];


  return (
    <motion.section
      ref={ref} 
      style={sectionStyle}
      variants={containerVariants} 
      initial="hidden" 
      animate={controls} 
    >
      <motion.h2 style={titleStyle} variants={itemVariants}> 
        See It Before You Stay
      </motion.h2>

      <motion.div style={categoriesStyle} variants={containerVariants}> 
        {photoCategories.map((category) => (
          <motion.button
            key={category.name}
            style={{
              ...categoryButtonStyle,
              ...(hoveredCategory === category.name && categoryButtonHoverStyle),
            }}
            onMouseEnter={() => setHoveredCategory(category.name)}
            onMouseLeave={() => setHoveredCategory(null)}
            onClick={() => console.log(`View photos of ${category.name}`)}
            whileHover={{ scale: 1.05, boxShadow: '0 6px 12px rgba(0, 0, 0, 0.1)' }}
            transition={{ type: "spring", stiffness: 300 }}
            variants={itemVariants} 
          >
            <motion.img
              src={category.imageUrl}
              alt={category.name}
              style={{
                ...categoryImageStyle,
                 ...(hoveredCategory === category.name && categoryImageHoverStyle),
              }}
            />
            <span style={categoryTextStyle}>{category.name}</span>
          </motion.button>
        ))}
      </motion.div>

      <motion.a
        href="/gallery"
        style={{
            ...ctaButtonStyle,
             ...(isCtaHovered && ctaButtonHoverStyle),
        }}
        onMouseEnter={() => setIsCtaHovered(true)}
        onMouseLeave={() => setIsCtaHovered(false)}
        whileHover={{ scale: 1.03, boxShadow: '0 8px 16px rgba(0, 0, 0, 0.2)' }}
        transition={{ type: "spring", stiffness: 300 }}
        variants={itemVariants} 
      >
        View Full Gallery
      </motion.a>
    </motion.section>
  );
};

export default GallerySection;

const categoryButtonHoverStyle = {
    backgroundColor: '#9b111e',
    borderColor: '#daa520',
    color: '#333',
  };

  const categoryImageHoverStyle = {
    transform: 'scale(1.1)',
  };

  const ctaButtonHoverStyle = {
    backgroundColor: '#b8860b',
  };